import axios from "../setup/axios";
const LoginUser = (data) => {
  const formData = new FormData();
  formData.append("Email", data.email);     // Đảm bảo sử dụng "Email" như Flask mong đợi
  formData.append("Password", data.password);

  return axios
    .post("/login", formData, {
      withCredentials: true,
      headers: { "Content-Type": "application/x-www-form-urlencoded" }  // Gửi đúng định dạng
    })
    .then((response) => {
      return response.data;
    })
    .catch((error) => {
      console.error("Login failed", error);
      throw error;
    });
};

const CreateNewUser = (data) => {
	return axios
		.post("/api/create-new-user", data,{
			withCredentials: true, 
		  })
		.then((response) => {
			return response;
		})
		.catch((error) => {
			console.error(error);
		});
};
const GetAllUser = (InputId) => {
	return axios
		.get(`/api/get-all-user?id=${InputId}`)
		.then((response) => {
			return response;
		})
		.catch((err) => {
			console.log(err);
		});
};
const getUserAccount = () => {
	return axios.get("/api/account");
};
const LogOutUser = () => {
	return axios.post("/api/logout");
};
const EditUserService = (user_edit) => {
	return axios.put("/api/edit-user", user_edit);
};
const DeleteUser = (idUser) => {
	return axios.delete("/api/delete-user", { data: { id: idUser } });
};
export {
	LoginUser,
	CreateNewUser,
	GetAllUser,
	getUserAccount,
	LogOutUser,
	EditUserService,
	DeleteUser,
};
